import React, { useState } from 'react';

export default function VerifyCode({ email, onSubmit, stepLabel }) {
  const [code, setCode] = useState('');
  const valid = /^\d{6}$/.test(code);

  function handleSubmit(e) {
    e.preventDefault();
    if (valid) onSubmit(code);
  }

  return (
    <form onSubmit={handleSubmit}>
      <div className="placeholder-img">IMAGE</div>
      <div className="step-label">{stepLabel}</div>
      <h2>Almost there!</h2>
      <div style={{ color: '#fff', marginBottom: 8, fontSize: '1rem' }}>
        A verification code has been sent to your email<br /><b>{email}</b>
      </div>
      <input
        type="text"
        placeholder="123 456"
        value={code}
        onChange={e => setCode(e.target.value.replace(/\D/g, '').slice(0,6))}
        required
      />
      <br />
      <button type="submit" disabled={!valid}>CONTINUE</button>
      <br />
      <button type="button" style={{ background: '#eee', color: '#232136' }} disabled>GO BACK</button>
    </form>
  );
}
